package slam4_sujet3_objMetier;

public class Region {
	 
	private int reg_num = -1;
	private String reg_nom;
	
	
	
	public Region(String reg_nom) {
		super();
		// TODO Auto-generated constructor stub
		this.reg_nom = reg_nom;
		
	}



	public Region(int reg_num, String reg_nom) {
		super();
		// TODO Auto-generated constructor stub
		this.reg_num = reg_num;
		this.reg_nom = reg_nom;
		
	}



	public int getReg_num() {
		return reg_num;
	}



	public void setReg_num(int reg_num) {
		this.reg_num = reg_num;
	}



	public String getReg_nom() {
		return reg_nom;
	}



	public void setReg_nom(String reg_nom) {
		this.reg_nom = reg_nom;
	}
}

